import { styled } from "styled-components"
import { Facebook, Instagram, LinkedIn, Logo, Twitter } from "../../../Assets/Svgs"
export const Footer = () => {
    return (
        <Container>
            <FooterBody>
                <div>
                    <Logo />
                    <p>Building the best and more efficient product for businesses</p>
                </div>

                <LinksContainer>
                    <div>
                        <h6>Product</h6>
                        <p>Overview</p>
                        <p>Features</p>
                        <p>Solutions</p>
                        <p>Tutorials</p>
                        <p>Pricing</p>
                    </div>

                    <div>
                        <h6>Company</h6>
                        <p>About us</p>
                        <p>Careers</p>
                    </div>


                    <div>
                        <h6>Resources</h6>
                        <p>Blog</p>
                        <p>Newsletter</p>
                        <p>Events</p>
                        <p>Help centre</p>
                    </div>
                </LinksContainer>

                <div>
                    <h6>
                        Stay up to date
                    </h6>
                    <p>Join over 5,000+ people in our community!</p>
                </div>
            </FooterBody>

            <FooterBottom>
                <p>All rights reserved Â© 2023 Ginger Technologies, Inc. </p>

                <IconContainer>
                    <Icon>
                        <Instagram />
                    </Icon>
                    <Icon>
                        <Facebook />
                    </Icon>
                    <Icon>
                        <Twitter />
                    </Icon>
                    <Icon>
                        <LinkedIn />
                    </Icon>
                </IconContainer>

            </FooterBottom>

        </Container>
    )
}


const Container = styled.footer`

`

const Icon = styled.div`
    padding: 8px;
    background-color: var(--gray-200);
    border-radius: 6px;
    transition: all 0.5s ease-in-out;
    &:hover{
        cursor: pointer;
        transform: scale(1.1);
        background-color: gray;
    }
`

const IconContainer = styled.aside`
    display: flex;
    align-items: center;
    gap: 1rem;
`

const FooterBottom = styled.section`
    width: 100%;
    background-color: var(--gray-100);
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 3% 5%;
`

const FooterBody = styled.div`
    padding: 5%;
    display: flex;
    align-items: center;
    justify-content: space-between;
`

const LinksContainer = styled.div`
    display: flex;
    justify-content: space-evenly;
    gap: 30%;
`
